mapper.select:
select m.*,(select p.name from sys_menu p where p.menu_id = m.parent_id) as parentName from sys_menu m
AND (select p.name from sys_menu p where p.menu_id = m.parent_id) like concat('%',#{parentName},'%')
AND m.name like concat('%',#{menuName},'%')
order by ${sort} ${order}
limit #{offset}, #{limit}

mapper.delete:
delete from sys_menu where menu_id in

	#{menuId}

;
delete from sys_role_menu where menu_id in

	#{menuId}
